/*

	In a N x N grid representing a field of cherries, each cell is one of three possible integers.

- 0 means the cell is empty, so you can pass through;
- 1 means the cell contains a cherry, that you can pick up and pass through;
- -1 means the cell contains a thorn that blocks your way.

	Your task is to collect maximum number of cherries possible by following the rules below:

- Starting at the position (0, 0) and reaching (N-1, N-1) by moving right or down through valid path cells (cells with value 0 or 1);
- After reaching (N-1, N-1), returning to (0, 0) by moving left or up through valid path cells;
- When passing through a path cell containing a cherry, you pick it up and the cell becomes an empty cell (0);
- If there is no valid path between (0, 0) and (N-1, N-1), then no cherries can be collected.

*/

package main

import (
	"fmt"
	"math/rand"
	"time"
)

const MaxGridSize = 50
const MinGridSize = 10

type node struct {
	X int
	Y int
}

func random(min, max int) int {
	return rand.Intn(max-min) + min
}

func createRandom2DGrid() [][]int {
	var n = random(MinGridSize, MaxGridSize)
	fmt.Println("Grid Size :: ", n)

	a := make([][]int, n)
	for i := range a {
		a[i] = make([]int, n)
	}

	for x, b := range a {
		for y := range b {
			a[x][y] = random(0, 3) - 1
			if a[x][y] == -1 {
				a[x][y] = random(0, 3) - 1
			}
		}
	}

	if a[0][0] == -1 {
		a[0][0] = random(0, 2)
	}

	if a[n-1][n-1] == -1 {
		a[n-1][n-1] = random(0, 2)
	}

	return a
}

func print2DGrid(grid [][]int) {

	for _, a := range grid {
		for _, b := range a {
			if b == -1 {
				fmt.Print("███")
			} else if b == 0 {
				fmt.Print("░░░")
			} else if b == 2 {
				fmt.Print(" × ")
			} else {
				fmt.Print(" · ")
			}
		}
		fmt.Println()
	}
}

func validCell(v node, grid [][]int) bool {
	return (v.X >= 0) &&
		(v.X < len(grid[0])) &&
		(v.Y >= 0) &&
		(v.Y < len(grid[0])) &&
		grid[v.X][v.Y] != -1
}

func max(x int, y int) int {
	if x > y {
		return x
	}
	return y
}

func min(x int, y int) int {
	if x < y {
		return x
	}
	return y
}

func fillDoubleArr(fill int, dim int) [][]int {
	arr := make([][]int, dim)
	for i := 0; i < dim; i++ {
		arr[i] = make([]int, dim)
		for j := 0; j < dim; j++ {
			arr[i][j] = fill
		}
	}
	return arr
}

func cherryPickup(grid [][]int) int {
	N := len(grid[0])
	dp := fillDoubleArr(-100, N)

	dp[0][0] = grid[0][0]

	for t := 1; t <= 2*N-2; t++ {
		dp2 := fillDoubleArr(-100, N)

		for i := max(0, t-(N-1)); i <= min(N-1, t); i++ {

			for j := max(0, t-(N-1)); j <= min(N-1, t); j++ {

				if grid[i][t-i] == -1 || grid[j][t-j] == -1 {
					continue
				}

				val := grid[i][t-i]

				if i != j {
					val += grid[j][t-j]
				}

				for pi := i - 1; pi <= i; {

					for pj := j - 1; pj <= j; {

						if pi >= 0 && pj >= 0 {
							dp2[i][j] = max(dp2[i][j], dp[pi][pj]+val)
						}
						pj++
					}
					pi++
				}
			}
		}

		dp = dp2
	}
	return max(0, dp[N-1][N-1])
}

